#define ASSEMBLER
#include "common.h"

	PROLOGUE
	PROFCODE

	cmpq	$4096, %rax
	jle	.L999
	ALIGN_3

.L01:
	subq	$4096, %rax
	subq	$4096, %rsp
	movq	$0, (%rsp)
	cmpq	$4096, %rax
	jg	.L01
	ALIGN_3
	
.L999:
	subq	%rax, %rsp
	ret

	EPILOGUE
